#include "./stdc++.h"

using namespace std;



void print_v(vector<int> &ret) {
	copy(ret.begin(), ret.end(), ostream_iterator<int>(cout, ", "));
	cout << endl;
}
class Solution {
public:
    void findSubstring(string &s, vector<string>& words, int start, map<string, int> m, vector<int> &ret) {
    	if (words.size() <= 0) return;
    	int left = start, right = start, step = words[0].length(), total = step * words.size(), count = words.size();
    	for (; left < s.length() && left <= right && right < s.length(); ) {
    		while (right - left < total) {
    			string t = s.substr(right, step);
    			// cout << t << endl;
    			if (m.count(t) > 0) {
    				if (m[t] > 0) {
    					count--;
    				}
    				m[t]--;
    			}
    			right += step;
    		}
    		if (count == 0) {
    			ret.push_back(left);
    		}
    		string t = s.substr(left, step);
    		// cout << t << endl;
    		// cout << endl;
			if (m.count(t) > 0) {
				if (m[t] >= 0) {
					count++;
				}
				m[t]++;
			}
			left += step;
    	}
    }
    vector<int> findSubstring(string s, vector<string>& words) {
    	vector<int> ret;
    	if (words.size() <= 0) return ret;
    	map<string, int> m;
    	for (int i = 0; i < words.size(); i++) {
    		if (m.count(words[i]) <= 0) {
    			m[words[i]] = 1;
    		} else {
    			m[words[i]]++;
    		}
    	}
    	for (int i = 0; i < words[0].length(); i++) {
    		vector<int> t;
    		findSubstring(s, words, i, m, t);
    		ret.insert(ret.end(), t.begin(), t.end());
    	}
    	return ret;
    }
};

